mapper.update:
update message
set message_status = #{messageStatus}
where message_id = #{messageId}
  and del_flag = 0

update message
set receive_message_ack = #{receiveMessageAck}
where message_id = #{messageId}
  and del_flag = 0

update message
set send_message_ack = #{sendMessageAck}
where message_id = #{messageId}
  and del_flag = 0
